mapper.select:
  select *
  from uni_application



          and app_name like concat('%', #{params.appName}, '%')


          and uni_app_type_id=#{params.uniAppTypeId}



          and uni_client_code=#{params.uniClientCode}




order by sort

SELECT ui.id FROM system_application sa INNER JOIN `uni_application` ui ON sa.id = ui.application_id WHERE sa.code IN
#{code}
AND ui.uni_client_code = #{clientCode}
